package mil.arl.gift.common.experiment;

import java.io.Serializable;

/**
 * An RPC-safe and client-safe representation of an experiment subject ID. This class exists to deal with common cases where 
 * {DbExperimentSubjectId mil.arl.gift.ums.db.table.DbExperimentSubject.DbExperimentSubjectID DbExperimentSubjectId} cannot be used, such as in the 
 * Javascript generated by GWT for web clients or in remote procedure calls made between web clients and servers.
 * 
 * @author nroberts
 */
public class ExperimentSubjectId implements Serializable {
	
	private static final long serialVersionUID = -3449863746309592830L;
	
	/** The ID of the subject represented by this object */
	private int subjectId;
	
	/** The ID of the experiment the subject participated in */
	private String experimentId;
	
	/**
     * Public no-arg constructor required by GWT RPC
     */
	public ExperimentSubjectId(){
		
	}
	
	/**
	 * Creates a new experiment subject ID
	 * 
	 * @param subjectId	the subject's ID
	 * @param experimentId the experiment's ID
	 */
	public ExperimentSubjectId(int subjectId, String experimentId){
		
		this.subjectId = subjectId;	
		this.experimentId = experimentId;
	}

	/**
	 * Gets the subject's ID
	 * 
	 * @return the subject's ID
	 */
	public int getSubjectId(){
        return subjectId;
    }
    
	/**
	 * Sets the subject's ID
	 * 
	 * @param subjectId the subject's ID
	 */
    public void setSubjectId(int subjectId){
        this.subjectId = subjectId;
    }
    
    /**
     * Gets the experiment's Id
     * 
     * @return the experiment's ID
     */
    public String getExperimentId() {
        return experimentId;
    }

    /**
     * Sets the experiment's ID
     * 
     * @param experimentId the experiment's ID
     */
    public void setExperiment(String experimentId) {
        this.experimentId = experimentId;
    }
    
    @Override
   	public String toString() {

   		StringBuilder sb = new StringBuilder();
   		sb.append("[Experiment:");
   		sb.append(" subjectId = ").append(getSubjectId());
   		sb.append(", experimentId = ").append(getExperimentId());
   		sb.append("]");

   		return sb.toString();
   	}
}
